namespace Octopy\Debug\DebugBar\Collector;

class FileCollector
{
    /**
     * @var string
     */
    public $title = 'File';

    /**
     * @return array
     */
    public function collect() : array
    {
        return get_included_files();
    }

    /**
     * @return string
     */
    public function icon() : string
    {
        return 'data:image/svg+xml;utf8;base64,';
    }
}
