namespace Octopy\Debug\DebugBar\Collector;

use Closure;
use Octopy\Support\Route;

class RouteCollector
{
    /**
     * @var string
     */
    public $title = 'Route';

    /**
     * @return Request
     */
    public function collect() : array
    {
        $route = Route::debugbar();
        foreach ($route['middleware'] as $i => $middleware) {
            if ($middleware instanceof Closure) {
                $route['middleware'][$i] = 'Closure';
            }
        }

        if ($route['action'] instanceof Closure) {
            $route['action'] = 'Closure';
        }

        return $route;
    }

    public function icon() : string
    {
        return 'data:image/svg+xml;utf8;base64,';
    }
}
